import timeit


def get_counts(string):
    stuart_count = 0
    kevin_count = 0
    string_length = len(string)
    vowels = ['A', 'E', 'I', 'O', 'U']
    for i, c in enumerate(string):
        amount = string_length - i
        if c in vowels:
            kevin_count += amount
        else:
            stuart_count += amount
    return kevin_count, stuart_count


def minion_game(string):
    kevin_count, stuart_count = get_counts(string)
    if stuart_count == kevin_count:
        print('Draw')
        return
    winner = 'Stuart' if stuart_count > kevin_count else 'Kevin'
    score = stuart_count if stuart_count > kevin_count else kevin_count
    print('{} {}'.format(winner, score))


if __name__ == '__main__':
    s = input()
    # minion_game(s)
    print(timeit.repeat(lambda: minion_game(s), number=1, repeat=10))

